export function CurrentWeather({ city, weatherData, unit }) {
    return (
        <div>
            {weatherData &&

                <div className="onfo">
                    <h1 className="center">{city}'s Weather </h1>
                    <p>Temperature :
                        {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp - 273.15) * 9 / 5 + 32)}&deg;
                        {unit === 'metric' ? 'C' : 'F'}</p>
                    <p>Min_Temperature : {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp_min - 273.15) * 9 / 5 + 32)}&deg;
                        {unit === 'metric' ? 'C' : 'F'}</p>
                    <p>MAX_Temperature : {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp_max - 273.15) * 9 / 5 + 32)}&deg;
                        {unit === 'metric' ? 'C' : 'F'}</p>
                    <p>Humidity : {weatherData.main.humidity}%</p>
                    <p>Wind Speed : {weatherData.wind.speed} m/s</p>
                    <p>Wind Direction : {weatherData.wind.deg}&deg;</p>
                    <p>Description : {weatherData.weather[0].description}</p>
                    <img src={`https://openweathermap.org/img/w/${weatherData.weather[0].icon}.png`}
                        alt="Weather Icon"></img>
                </div>}
        </div>
    );
}
