export function CurrentWeather({ city, weatherData, unit }) {
    return (
        <div>
            {weatherData &&

                <div className="onfo">
                    <h1 className="center">{city}'s Weather </h1>
                    <div className="maincontainer"> 
                    
                        <div className="leftcontainer">
                            <h1>Now</h1>
                            <p className="bigfont">
                            {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp - 273.15) * 9 / 5 + 32)}&deg;
                            {unit === 'metric' ? 'C' : 'F'}</p>
                            <p>min: {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp_min - 273.15) * 9 / 5 + 32)}&deg;
                                {unit === 'metric' ? 'C' : 'F'}</p>
                            <p>max: {unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp_max - 273.15) * 9 / 5 + 32)}&deg;
                                {unit === 'metric' ? 'C' : 'F'}</p>
                            
                        </div>
                      
                      
                        <div>
                            <img className="image" src={`https://openweathermap.org/img/w/${weatherData.weather[0].icon}.png`}
                            alt="Weather Icon"></img>
                            <div >
                            <h3>{weatherData.weather[0].description}</h3>
                            <div>humidity:{weatherData.main.humidity}%</div>
                            <div> Wind:{weatherData.wind.speed} m/s</div>
                            <div>Dir:{weatherData.wind.deg}&deg;</div>
                            
                            </div>
                        </div>

                    </div>
                </div>}
        </div>
    );
}
