import { useEffect, useState } from "react";


function Input(){

    // let api_key = "5a0d91aa2eb889bc8ed1ce44f7aac4c2";
    const[searchcity,setSearchCity] = useState("Pune")
    const[cityNotFound,setCityNotFound] = useState(false)

    const [weatherData, setWeatherData] = useState(null)
    const [celsius,setCelsius] = useState(false)
    useEffect(() => { 
        const fetchApi = async () => {
            if(!searchcity){
                return
            }

            try{

                const url = `https://api.openweathermap.org/data/2.5/weather?q=${searchcity}&appid=5a0d91aa2eb889bc8ed1ce44f7aac4c2`;
                const response = await fetch(url);
                
                const resJSON =  await response.json();
                if( resJSON.cod ==="404"){

                    setCityNotFound(true)
                }
                else{

                    console.log(resJSON);
                    // console.log(resJSON.main.temp);
                    setWeatherData(resJSON)
                    setCityNotFound(false)
                }
            }
            
            catch(error){
                    console.error('Error fetching weather Data',error)
            }

        };

        fetchApi()

    },[searchcity])
    // console.log(input)
    // console.log(searchcity)

    function celsiusConverter(){
        setCelsius(true)
    }

    let currTemp = weatherData.main.temp
    let mintemp = weatherData.main.temp_min
    let maxtemp = weatherData.main.temp_max

   let finaltemp =  (`${celsius} ? ${currTemp-273}°C : ${currTemp}°F `)


    return(
        <>
        <div className="inputdata">
        <input type ="text"  onChange={(e) => setSearchCity(e.target.value)}/>
        <button>Search</button>
        <button onClick={celsiusConverter}>Celsius</button>
        </div>

        {cityNotFound ? <p>NO Data found</p> : 

        <div className="onfo">
            <h2>
            <i className="fa-solid fa-location-dot"></i>{weatherData ? weatherData.name :"Loading"}
            </h2>

        <h1>
            {weatherData ? finaltemp :"loading"}
           
        </h1>

        <h3>min :
             {weatherData ? mintemp:"loading"}°C
        |  max :
         {weatherData ? maxtemp:"loading"}°C
         </h3>

        </div>
        
         } 

        </>
        
    )
}

export default Input;
