import { useEffect, useState } from "react";


function Input(){

    let api_key = "5a0d91aa2eb889bc8ed1ce44f7aac4c2";
    const[searchcity,setSearchCity] = useState("Mumbai")
    // console.log(city);
    const [city, setCity] = useState("Pune")

    useEffect(() => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${searchcity}&appid=${api_key}`;
            const response = await fetch(url);
            
            const resJSON =  await response.json();
            // console.log(resJSON);
            // setSearchCity(resJSON.main);
            // console.log(resJSON.main.temp);
            // setCity(resJSON.main)
        }

        fetchApi()

    },[searchcity])

    function clickHandler(){
        // setCity(searchcity)
    }
    return(
        <>
        <div className="inputdata">
        <input type ="text" value={searchcity}  onChange={(e) => setSearchCity(e.target.value) } />
        <button onClick={clickHandler}>Search</button>
        </div>

        {!city ? <p>NO Data found</p> :

        <div className="onfo">
            <h2>
            <i className="fa-solid fa-location-dot"></i>{searchcity.name}
            </h2>

        <h1>
            52 Cel
        </h1>

        <h3>Min : 100 deg cel  |  Max : 60 cel</h3>

        </div>
         } 

        </>
        
    )
}

export default Input;
