import { useEffect, useState } from "react";
import axios from "axios";



function WeatherApp(){

    // let api_key = "5a0d91aa2eb889bc8ed1ce44f7aac4c2";
    const[city,setCity] = useState("Pune")
    const [weatherData, setWeatherData] = useState(null)
    const [unit,setUnit] = useState("metric")

    const API_KEY = "5a0d91aa2eb889bc8ed1ce44f7aac4c2"
    useEffect(() => { 
        
            if(city)
            {               
               axios
               .get(
                `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=5a0d91aa2eb889bc8ed1ce44f7aac4c2`
               )
            
               .then((response) => {
                setWeatherData(response.data)
                }) 
                
            .catch((error) => {
                console.error('Error fetching', error)
            });
        }
    },[city,unit]);
   

    function handleCityChange(e){
        setCity(e.target.value)
    }

    function handleUnitToggle(){
        setUnit((prevUnit) => (prevUnit === 'metric' ? 'imperial' : "metric"));
    }


    return(
        <>
        <div className="inputdata">
        <input type ="text" value={city}  onChange={handleCityChange}/>
        
        <button onClick={handleUnitToggle}>
            Toggle Unit ({unit === "metric" ? "Celsius" : "Fahrenheit"})
        </button>
        </div>

        {weatherData &&
       
        <div className="onfo">
            <h1>Current weather</h1>
            <p>Temperature : {weatherData.main.temp}&deg;{unit === 'metric' ? 'C' :'F'}</p>
            <p>Min_Temperature : {weatherData.main.temp_min}&deg;{unit === 'metric' ? 'C' :'F'}</p>
            <p>MAX_Temperature : {weatherData.main.temp_max}&deg;{unit === 'metric' ? 'C' :'F'}</p>
            <p>Humidity : {weatherData.main.humidity}%</p>
            <p>Wind Speed : {weatherData.wind.speed} m/s</p>
            <p>Wind Direction : {weatherData.wind.dir}&deg;</p>
            <p>Description : {weatherData.weather[0].description}</p>
            <img alt= "Weather Icon"></img>
         </div>
        
         } 

        </>
        
    )
}

export default WeatherApp;
