export function Forecast({ forecastData, unit, weatherData }) {
    return (
        <div>
            {forecastData && (
                <div>
                    <h2> 5 Day Forecast</h2>
                    <div>
                        {forecastData.slice(0, 5).map((forecast) => (
                            <div key={forecast.dt}>
                                <p>Date : {new Date(forecast.dt * 1000).toLocaleDateString()}</p>
                                <p> Average Temp :{unit === 'metric' ? Math.round(weatherData.main.temp - 273.15) : Math.round((weatherData.main.temp - 273.15) * 9 / 5 + 32)}&deg;
                                    {unit === 'metric' ? 'C' : 'F'}</p>
                                <p>Description : {forecast.weather[0].description}</p>
                                <img src={`http://openweathermap.org/img/w/${forecast.weather[0].icon}.png`}
                                    alt="Weather Icon"></img>
                            </div>
                        ))}
                    </div>
                </div>
            )}

        </div>
    );
}
