import { useEffect, useState } from "react";


function Input(){

    let api_key = "5a0d91aa2eb889bc8ed1ce44f7aac4c2";
    const[input,setInput] = useState()
    const[searchcity,setSearchCity] = useState("Mumbai")
    // console.log(city);
    const [citydata, setCitydata] = useState()

    useEffect(() => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${searchcity}&appid=${api_key}`;
            const response = await fetch(url);
            
            const resJSON =  await response.json();
            // console.log(resJSON);
            console.log(resJSON);
            console.log(resJSON.main.temp);
            setCitydata(!resJSON ?<p>NO data</p> : resJSON)
        }

        fetchApi()

    },[searchcity])
    console.log(input)
    console.log(searchcity)

    function clickHandler(){
        setSearchCity(input)
    }
    return(
        <>
        <div className="inputdata">
        <input type ="text" value={input} onChange={(e) => setInput(e.target.value)}/>
        <button onClick={clickHandler}>Search</button>
        </div>

        {!citydata ? <p>NO Data found</p> :

        <div className="onfo">
            <h2>
            <i className="fa-solid fa-location-dot"></i>{citydata.name}
            </h2>

        <h1>
            {citydata.main.temp} Cel
        </h1>

        <h3>Min : {citydata.main.temp_min} deg cel  |  Max : {citydata.main.temp_max} cel</h3>

        </div>
         } 

        </>
        
    )
}

export default Input;
