import { useEffect, useState } from "react";


function Input(){
    const[searchcity,setSearchCity] = useState("Pune")
    // console.log(city);
    // const [search, setSearch] = useState("Mumbai")

    useEffect(() => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${searchcity}&appid=5a0d91aa2eb889bc8ed1ce44f7aac4c2`;
            const response = await fetch(url);
            
            const resJSON = response.json();
            // console.log(resJSON);
            setSearchCity(resJSON.main);
            console.log(searchcity);
        }

        fetchApi()

    },[searchcity])

    // function clickHandler(){
    //     s
    // }
    return(
        <>
        <div className="inputdata">
        <input type ="text" value={searchcity}  onChange={(e) => setSearchCity(e.target.value) } />
        <button>Search</button>
        </div>

        {/* {!city ? <p>NO Data found</p> : */}

        <div className="onfo">
            <h2>
            <i className="fa-solid fa-location-dot"></i>{searchcity}
            </h2>

        <h1>
            52 Cel
        </h1>

        <h3>Min : 100 deg cel  |  Max : 60 cel</h3>

        </div>
        {/* } */}

        </>
        
    )
}

export default Input;
